drop table if exists medicaments CASCADE;
drop table if exists patients CASCADE;
drop table if exists prescriptions CASCADE;
create table medicaments (id bigint generated by default as identity, exclusive_use varchar(255), max_age_consumption smallint, minimum_age_consumption smallint, name varchar(255), primary key (id));
create table patients (id bigint generated by default as identity, birth_date date not null, enabled boolean not null, gender varchar(255) not null, last_name varchar(100) not null, name varchar(100) not null, primary key (id))
create table prescriptions (id bigint generated by default as identity, date_prescription date, medicament_id bigint, patient_id bigint, primary key (id));
alter table prescriptions add constraint FK3asoi2xynomsq3wfpjpcefaia foreign key (medicament_id) references medicaments;
alter table prescriptions add constraint FKqydyol76jn1o37k1bdbkjgq74 foreign key (patient_id) references patients;




